mapper.select:
select c1.company_id ,c1.simple_comment ,c1.advantages ,c1.disadvantages ,c2.all_point ,c1.rec_create_date ,c1.company_review_id from
blind.company_review_mgt c1 inner join ( select all_point ,company_id ,company_review_id from blind.company_review_mgt where company_review_id = ( select c3.company_review_id from ( select company_review_id ,count(helpful) as cnt from blind.review_recommend_inf where company_id=#{companyId} group by company_review_id order by cnt desc fetch first 1 rows only ) c3
)
)c2 on c1.company_review_id=c2.company_review_id

mapper.insert:
INSERT INTO blind.review_recommend_inf(

       review_recommend_id
       , company_review_id
       , company_id
       , user_id
       , helpful
)
VALUES (
           default
       , #{companyReviewId}
       , #{companyId}
       ,#{userId}
       ,1
       );
