mapper.insert:
insert into
	blind.company_review_mgt(
		company_review_id
		,user_id
		,company_id
		,job_group_code
		,all_point
		,career_point
		,work_life_balance_point
		,pay_point
		,company_culture_point
		,head_point
		,simple_comment
		,advantages
		,disadvantages
		,resign_reason
		,work_start_date
		,work_end_date
		,work_area
		,verify_flag
		,complete_flag
	    , rec_create_user_id
        , rec_create_date
        , rec_update_user_id
        , rec_update_date
        , logical_del_flag

	)values(
		default
		,#{userId}
		,#{companyId}
		,#{jobGroupCode}
		,#{allPoint}
		,#{careerPoint}
		,#{workLifeBalancePoint}
		,#{payPoint}
		,#{companyCulturePoint}
		,#{headPoint}
		,#{simpleComment}
		,#{advantages}
		,#{disadvantages}
		,#{resignReason}
		,#{workStartDate}
		,#{workEndDate}
		,#{workArea}
		,'0'
		,'0'
	    , 1
        , now()
        , 0
        , now()
        , '0'	
	)

mapper.select:
SELECT post_title --게시글 주제 ,post_contents --게시글 내용 ,company_name --작성자가 근무하는 기업명 ,user_nickname --작성자의 닉네임 ,(SELECT COUNT(*) FROM blind.post_count_inf AS pci WHERE po.post_id = pci.post_id) AS post_count --글의 조회수 ,(SELECT COUNT(*) AS recommend_count FROM blind.post_recommend_inf as pri where pri.post_id = po.post_id) AS recommend_count --글의 추천수 ,(SELECT COUNT(*) AS reply_count FROM blind.reply_mgt as rm where rm.post_id = po.post_id) AS reply_count --글의 댓글수 ,post_create_date --게시글의 최초작성일 FROM blind.post_mgt as po LEFT JOIN blind.user_mgt as us --작성자의 닉네임 ON po.user_id = us.user_id and last_generation_flag = '1' LEFT JOIN blind.company_mgt as cm --근무기업명 ON us.company_id = cm.company_id where ( post_title like '%' || #{companyName} || '%' or post_contents like '%' || #{companyName} || '%')
and ( post_title like '%' || #{searchKeyword} || '%' or post_contents like '%' || #{searchKeyword} || '%')
AND po.logical_del_flag = '0' order by po.post_id DESC;
