mapper.select:
SELECT COUNT(*) FROM blind.company_mgt AS co
INNER JOIN blind.business_type_mst AS bu ON co.business_type_code = bu.business_type_code
WHERE co.logical_del_flag = '0'
AND verify_flag = #{verifyFlag}
AND closing_flag = #{closingFlag}
AND LOWER(
company_name business_type_name company_domain company_address
) LIKE '%' || LOWER(#{searchKeyword}) || '%' ;

SELECT co.company_id AS companyId, verify_flag AS verifyFlag, closing_flag AS closingFlag, company_name AS companyName, co.business_type_code AS businessTypeCode, company_address AS companyAddress, TO_CHAR(founding_date, 'YYYY-MM-DD') AS foundingDate, workers_count AS workersCount, company_domain AS companyDomain, company_homepage AS companyHomepage, company_explain AS companyExplain, user_nickName AS userNickName, TO_CHAR(app_date, 'YYYY-MM-DD') AS appDate FROM blind.company_mgt AS co INNER JOIN blind.user_mgt AS us ON co.user_id = us.user_id
INNER JOIN blind.business_type_mst ON co.business_type_code = business_type_mst.business_type_code
WHERE co.logical_del_flag = '0'
AND verify_flag = #{verifyFlag}
AND closing_flag = #{closingFlag}
AND LOWER(
company_name business_type_name company_domain company_address
) LIKE '%' || LOWER(#{searchKeyword}) || '%'
ORDER BY companyId;
