mapper.select:
SELECT (
	SELECT post_count
	FROM blind.post_count_inf
	WHERE post_id = #{postId}
) AS postCount, (
	SELECT COUNT(*)
	FROM blind.reply_mgt
	WHERE post_id = #{postId}
		AND logical_del_flag = '0'
) AS replyCount, (
	SELECT COUNT(*)
	FROM blind.post_recommend_inf
	WHERE post_id = #{postId}
		AND post_recommend_flag = '1'
) AS postRecommendCount;

mapper.insert:
INSERT INTO blind.reply_mgt (
	post_id, target_reply_id, user_id, reply_file_url, reply_contents, reply_blind_flag,
	rec_create_user_id, rec_create_date, rec_update_user_id, rec_update_date, logical_del_flag
) VALUES (
	#{postId}, #{targetReplyId}, #{userId}, #{replyFileUrl}, #{replyContents}, #{replyBlindFlag},
	#{userId}, NOW(), #{userId}, NOW(), '0'
);

mapper.select:
SELECT MAX(reply_id)
FROM blind.reply_mgt
WHERE user_id = #{userId}
	AND post_id = #{postId};

SELECT reply_recommend_id
FROM blind.reply_recommend_inf
WHERE user_id = #{userId}
	AND reply_id = #{replyId};

mapper.insert:
INSERT INTO blind.reply_recommend_inf (
	reply_id, user_id, reply_recommend_date, reply_recommend_flag
) VALUES (
	#{replyId}, #{userId}, NOW(), '1'
);

mapper.select:
SELECT SUM(reply_recommend_flag) AS replyRecommendCount,
	EXISTS(
		SELECT
		FROM blind.reply_recommend_inf
		WHERE reply_id = #{replyId}
			AND user_id = #{userId}
			AND reply_recommend_flag = 1
	) AS recommended
FROM blind.reply_recommend_inf
WHERE reply_id = #{replyId}
